`timescale 1ns / 1ps
 
module testbench_labtest_combinational;
 
    integer count;
    
    // ------------------ Specify variables generated by testbench ------------------ 
    reg in1, in2, in3, in4;
    
    // ------------------ Specify variables that are watched by testbench ------------------ 
    wire out;
    
    labtest_q1_hardware myname(.a(in1),.b(in2),.c(in3),.d(in4),
                                .y(out));
 
    // ------------------ Setup inputs ------------------
    // Provide initial values for inputs. The ‘initial’ command
    // is only valid within the testbench.
    // Can use this for all simple testbenches
    initial begin 
        count = 0;
    end
 
    always begin
        #50
        count=count+1;
    end
 
    // Toggle inputs at different rates to cover all input  
    always @(count) begin
        case (count)
				0		  : begin in1<=1'b0; in2<=1'b0; in3<=1'b0; in4<=1'b0;end
            1       : begin in1<=1'b0; in2<=1'b0; in3<=1'b0; in4<=1'b1;end 
				2       : begin in1<=1'b0; in2<=1'b0; in3<=1'b1; in4<=1'b0;end 
				3       : begin in1<=1'b0; in2<=1'b0; in3<=1'b1; in4<=1'b1;end 
				4       : begin in1<=1'b0; in2<=1'b1; in3<=1'b0; in4<=1'b0;end 
				5       : begin in1<=1'b0; in2<=1'b1; in3<=1'b0; in4<=1'b1;end 
				6       : begin in1<=1'b0; in2<=1'b1; in3<=1'b1; in4<=1'b0;end 
				7       : begin in1<=1'b0; in2<=1'b1; in3<=1'b1; in4<=1'b1;end 
				8       : begin in1<=1'b1; in2<=1'b0; in3<=1'b0; in4<=1'b0;end 
				9       : begin in1<=1'b1; in2<=1'b0; in3<=1'b0; in4<=1'b1;end 
				10      : begin in1<=1'b1; in2<=1'b0; in3<=1'b1; in4<=1'b0;end 
				11      : begin in1<=1'b1; in2<=1'b0; in3<=1'b1; in4<=1'b1;end
				12      : begin in1<=1'b1; in2<=1'b1; in3<=1'b0; in4<=1'b0;end 
				13      : begin in1<=1'b1; in2<=1'b1; in3<=1'b0; in4<=1'b1;end 
				14      : begin in1<=1'b1; in2<=1'b1; in3<=1'b1; in4<=1'b0;end 
				15      : begin in1<=1'b1; in2<=1'b1; in3<=1'b1; in4<=1'b1;end 
            default : begin {in1, in2, in3, in4} = ($urandom());end 
        endcase
    end
 
endmodule