`timescale 1ns / 1ps
 
module testbench_labtest2_combinational;
 
    integer count;
    
    // ------------------ Specify variables generated by testbench ------------------ 
    reg[3:0] in1, in2, in3, in4;
	 reg in5, in6, in7, in8;
    
    // ------------------ Specify variables that are watched by testbench ------------------ 
    wire[3:0] out1;
    
    labtest_q2_hardware myname(.a(in1),.b(in2),
                                .c(in5),.d(in4),
                                .x(out1));
 
    // ------------------ Setup inputs ------------------
    // Provide initial values for inputs. The ‘initial’ command
    // is only valid within the testbench.
    // Can use this for all simple testbenches
    initial begin 
        count = 0;
    end
 
    always begin
        #50
        count=count+1;
    end
 
    // Toggle inputs at different rates to cover all input  
    always @(count) begin
        case (count)
            1 : begin in1<=4'b0000; in2<=4'b0000; in5<=1'b0; in4<=4'b0000; end 
            default : begin in1<=4'b0110; in2<=4'b1000; in5<=1'b0;  in4<=4'b0100; end 
        endcase
    end
 
endmodule
